mapper.select:
SELECT DISTINCT a.recipeNum, a.userId, userName, a.recipeSubject, a.recipeTime, b.recipePhotoName, NVL(likeCount, 0) likeCount, recipeReg_date from recipe a join member m ON a.userId=m.userId left outer join recipePhoto b on a.recipeNum = b.recipeNum left outer join ( select recipeNum, count(*) likeCount from recipeLike group by recipeNum ) c on a.recipeNum = c.recipeNum left outer join ( select d.recipeNum, d.ingredientCode from ingredientList d ) d on a.recipeNum = d.recipeNum left outer join ( select e.ingredientCode, e.parent from ingredient e ) e on d.ingredientCode = e.ingredientCode
a.caseNum IN
#{item}
