mapper.select:
SELECT userId, userName, userPwd, nickName, role, countryNum
FROM member
WHERE userId = #{userId}

mapper.update:
UPDATE member SET userPwd=#{userPwd}, modify_date=SYSDATE, email=#{email, jdbcType=VARCHAR}, 
	tel=#{tel, jdbcType=VARCHAR}, birth=#{birth, jdbcType=VARCHAR},
	zip=#{zip, jdbcType=VARCHAR},
	addr1=#{addr1, jdbcType=VARCHAR}, addr2=#{addr2, jdbcType=VARCHAR}
WHERE userId=#{userId}
